UPDATE UMS_DEST
SET
(
NOW_DATE
,SEND_DATE
,TCS_RESULT
)
=
(
SELECT
#{deliver_date, jdbcType=NVARCHAR}
,#{report_res_date, jdbcType=NVARCHAR}
,#{tcs_result}
FROM DUAL
)
WHERE
MSGID=#{id}

