mapper.update:
UPDATE UMS_DEST
SET
(
	NOW_DATE
	,SEND_DATE
	,TCS_RESULT
)
=
(
SELECT
	#{deliver_date}
	,#{report_res_date}
	,#{tcs_result}
FROM DUAL
)
WHERE
MSGID=#{id}

