mapper.insert:

	INSERT INTO UMS_DEST_TEMP (
		DESTID
		,DESTNM
		,DESTNUM
		,VAR1
		,VAR2
		,VAR3
		,VAR4
	) VALUES (
		DEST_TEMP_SEQ.NEXTVAL
		,#{destNm}
		,#{destNum}
		,#{var1}
		,#{var2}
		,#{var3}
		,#{var4}
	)

mapper.select:
SELECT count(*)
FROM UMS_DEST_TEMP
WHERE DESTNUM=#{destNum}

SELECT COUNT(1) FROM UMS_DEST_TEMP WHERE 1=1
AND DESTNM LIKE '% || ${destNm} || %'
AND DESTNM = #{destNum}

SELECT DESTNM ,DESTNUM ,VAR1 ,VAR2 ,VAR3 ,VAR4 FROM UMS_DEST_TEMP WHERE 1=1
AND DESTNM LIKE '% || ${destNm} || %'
AND DESTNM = #{destNum}

choose.when:

INSERT INTO UMS_MSG_SEND (
	MSGID
	,SENDTYPE
	,SCHDTYPE
	,SUBJECT
	,DEPARTNUM
	,NOWDATE
	,SENDDATE
	,MSGCNT
	,MSGCONTENT
	,ATTACHFILE
	,SUCCCNT
	,FAILCNT
	,RESERVED1
	,RESERVED2
) VALUES (
	#{msgId}
	,#{sendType, jdbcType=NVARCHAR}
	,#{schdType, jdbcType=NVARCHAR}
	,#{subject}
	,#{departNum}
	,to_char(sysdate,'YYYYMMDDHH24MISS')
	,#{sendDate}
	,#{msgCnt}
	,#{msgContent}
	,#{attachFile, jdbcType=NVARCHAR}
	,NULL
	,NULL
	,#{msgId}
	,#{reserved2, jdbcType=NVARCHAR}
)

choose.otherwise:

INSERT INTO UMS_MSG_SEND (
	MSGID
	,SENDTYPE
	,SCHDTYPE
	,SUBJECT
	,DEPARTNUM
	,NOWDATE
	,SENDDATE
	,MSGCNT
	,MSGCONTENT
	,ATTACHFILE
	,SUCCCNT
	,FAILCNT
	,RESERVED1
	,RESERVED2
) VALUES (
	#{msgId}
	,#{sendType, jdbcType=NVARCHAR}
	,#{schdType, jdbcType=NVARCHAR}
	,#{subject}
	,#{departNum}
	,to_char(sysdate,'YYYYMMDDHH24MISS')
	,to_char(sysdate,'YYYYMMDDHH24MISS')
	,#{msgCnt}
	,#{msgContent}
	,#{attachFile, jdbcType=NVARCHAR}
	,NULL
	,NULL
	,#{msgId}
	,#{reserved2, jdbcType=NVARCHAR}
)

choose.when:
