mapper.insert:

	INSERT INTO UMS_DEST_TEMP (
		DEST_ID
		,DEST_NAME
		,DEST_NUM
		,VAR1
		,VAR2
		,VAR3
		,VAR4
	) VALUES (
		DEST_TEMP_SEQ.NEXTVAL
		,#{destNm}
		,#{destNum}
		,#{var1}
		,#{var2}
		,#{var3}
		,#{var4}
	)

mapper.select:
SELECT COUNT(1) FROM UMS_DEST_TEMP
AND DEST_NAME LIKE '% || ${destNm} || %'
AND DEST_NAME = #{destNum}

SELECT DEST_NAME ,DEST_NUM ,VAR1 ,VAR2 ,VAR3 ,VAR4 FROM UMS_DEST_TEMP
AND DEST_NAME LIKE '% || ${destNm} || %'
AND DEST_NAME = #{destNum}

choose.when:

INSERT INTO UMS_MSG_SEND (
	MSGID
	,SENDTYPE
	,SCHDTYPE
	,SUBJECT
	,DEPARTNUM
	,NOWDATE
	,SENDDATE
	,MSGCNT
	,MSGCONTENT
	,ATTACHFILE
	,SUCCCNT
	,FAILCNT
	,RESERVED1
	,RESERVED2
) VALUES (
	SEND_SEQ.NEXTVAL
	,NULL
	,#{schdType, jdbcType=NVARCHAR}
	,#{subject}
	,#{departNum}
	,to_char(sysdate,'YYYYMMDDHH24MISS')
	,#{sendDate}
	,#{msgCnt}
	,#{msgContent}
	,#{attachFile, jdbcType=NVARCHAR}
	,NULL
	,NULL
	,#{reserved1, jdbcType=NVARCHAR}
	,#{reserved2, jdbcType=NVARCHAR}
)

choose.otherwise:

INSERT INTO UMS_MSG_SEND (
	MSGID
	,SENDTYPE
	,SCHDTYPE
	,SUBJECT
	,DEPARTNUM
	,NOWDATE
	,SENDDATE
	,MSGCNT
	,MSGCONTENT
	,ATTACHFILE
	,SUCCCNT
	,FAILCNT
	,RESERVED1
	,RESERVED2
) VALUES (
	SEND_SEQ.NEXTVAL
	,NULL
	,#{schdType, jdbcType=NVARCHAR}
	,#{subject}
	,#{departNum}
	,to_char(sysdate,'YYYYMMDDHH24MISS')
	,to_char(sysdate,'YYYYMMDDHH24MISS')
	,#{msgCnt}
	,#{msgContent}
	,#{attachFile, jdbcType=NVARCHAR}
	,NULL
	,NULL
	,#{reserved1, jdbcType=NVARCHAR}
	,#{reserved2, jdbcType=NVARCHAR}
)
