mapper.insert:

	INSERT INTO UMS_DEST_TEMP (
		DEST_ID
		,DEST_NAME
		,DEST_NUM
		,VAR1
		,VAR2
		,VAR3
		,VAR4
	) VALUES (
		DEST_TEMP_SEQ.NEXTVAL
		,#{destNm}
		,#{destNum}
		,#{var1}
		,#{var2}
		,#{var3}
		,#{var4}
	)

mapper.select:
SELECT COUNT(1) FROM UMS_DEST_TEMP
AND DEST_NAME LIKE '% || ${DEST_NM} || %'
AND DEST_NAME = #{DEST_NUM}

SELECT DEST_NAME ,DEST_NUM ,VAR1 ,VAR2 ,VAR3 ,VAR4 FROM UMS_DEST_TEMP
AND DEST_NAME LIKE '% || ${DEST_NM} || %'
AND DEST_NAME = #{DEST_NUM}

